using System;using System.Collections.Generic;using System.Linq;using Xamarin.Forms;using SecondOpinion.ViewModels;using SecondOpinion.Models;using SecondOpinion.Repositories;using ReactiveUI;using System.Reactive.Linq;using System.Collections.Specialized;using System.Reactive.Disposables;using Splat;

namespace SecondOpinion.Views.Chat {    public partial class ChatPage : ContentPageBase<DialogViewModel> {        UserLogin currentUser;        public ChatPage (Dialog chat) : base() {            InitializeComponent();            ViewModel.Chat = chat;            ViewModel.Populate();            SubscribeToViewModel();            currentUser = Locator.Current.GetService<ISettingsRepository>().GetUserLogin();        }        private void SubscribeToViewModel() {            this.WhenActivated(disposable => {                this.Bind(ViewModel , vm => vm.MessageText , v => v.MessageEditText.Text)                    .DisposeWith(SubscriptionDisposables);                this.BindCommand(ViewModel , vm => vm.SendMessageCommand , v => v.SendMessageButton)                    .DisposeWith(SubscriptionDisposables);                ViewModel.WhenAnyValue(vm => vm.MessageList)                         .ObserveOn(RxApp.MainThreadScheduler)                         .Subscribe(AddMessageToTableView);                ViewModel.MessageList.Changed                         .ObserveOn(RxApp.MainThreadScheduler)                         .Subscribe(AddMessageToTableView);            });        }        private void AddMessageToTableView(IList<Message> list) {            this.MessageList.ItemsSource = list.OrderBy(x => x.Id)                .Select(message => new {MessageBody = message.MessageBody, MessageColor = GetColor(message)});        }                private void AddMessageToTableView (NotifyCollectionChangedEventArgs eventArgs) {            this.MessageList.ItemsSource = ViewModel.MessageList.OrderBy(x => x.Id)                .Select(message => new { MessageBody = message.MessageBody , MessageColor = GetColor(message) });        }        private Color GetColor(Message message) {            return currentUser.UserId == message.ToUserId ? ColorUtils.MessageBlue : ColorUtils.MessageGreen;        }    }}